import {useState} from 'react'
//import {Navigate} from 'react-router-dom'
import axios from 'axios'
import {ModalWindow} from "../component/modal"


export const CreatePage = () => {

    //const [navigate, setNavigate] = useState( false )
    const [show, setShow] = useState(false)
    const [errorPassword,setErrorPassword] = useState(true)
    const [errorEmail,setErrorEmail] = useState(true)
    const [errorText,setErrorText] = useState(true)
    const [disable, setDisable] = useState(true)
    const [form, setForm] = useState({
        username: '', email: '', text: ''
    })



    const changeHandler = (event) => {
        setForm({...form, [event.target.name]: event.target.value})
    }

    const changeBlur = (event) => {
        switch (event.target.name){
            case 'username':
                if(event.target.value = ''){
                    setErrorPassword(true)
                    setDisable(true)

                }
                break
            case 'email':
                if(event.target.value = ''){
                    setErrorEmail(true)
                    setDisable(true)

                }
                break
            case 'text':
                if(event.target.value = ''){
                    setErrorText(true)
                    setDisable(true)

                }
                break
        }
    }

    const pressHandler = async () => {
        try{

            const formData = new FormData();
                formData.append("username", form.username);
                formData.append("email", form.email);
                formData.append("text", form.text);

            await axios({
                url: 'https://uxcandy.com/~shapoval/test-task-backend/v2/create?developer=ilia',
                crossDomain: true,
                    method: 'POST',
                    mimeType: "multipart/form-data",
                    contentType: false,
                    processData: false,
                    data: formData,
                    dataType: "json",
            }).then((response) => {
                //setNavigate(true)
                console.log(response.data.message)
                setShow(true)
            })


        }catch(e){}
    }



    return (
        <form style={{paddingTop: 100, maxWidth: 500, margin: 'auto'}} >
            <ModalWindow 
            message={message}
            show={show}
            />
            <div className="mb-3">
                <label htmlFor="exampleFormControlInput2" className="form-label">Username</label>
                <input onBlur={changeBlur} type="text" className="form-control" name='username' id="exampleFormControlInput2" onChange={changeHandler} />
            </div>
            <div className="mb-3">
                <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
                <input onBlur={changeBlur} type="email" className="form-control" name='email' id="exampleFormControlInput1" placeholder="name@example.com" onChange={changeHandler}/>
            </div>
            <div className="mb-3">
                <label htmlFor="exampleFormControlTextarea1" className="form-label">Example textarea</label>
                <textarea onBlur={changeBlur} className="form-control" name='text' id="exampleFormControlTextarea1" rows="3" onChange={changeHandler}/>
            </div>
            <button className="w-100 btn btn-lg btn-primary" disabled={disable} onClick={pressHandler} type='button'>Sign in</button>
        </form>
    )
}
