import axios from 'axios'
import {useState, useCallback, useEffect} from "react"

export const TasksPage = () => {
    const [tasksData, setTasksData] = useState([])
    const [loading, setLoading] = useState(false)
    const [numTasks, setNumTasks] = useState(0)
    const [page, setPage] = useState(1)

    const getTask = useCallback(async () => {
        try{
            setLoading(true)
            await axios.get(
                `/api/tasks/?page=${page}`
                ).then((response) => {
                    setLoading(false)
                    setTasksData(response.data.message.tasks)

                }, [page])  

        }catch(e){console.log(e)}

    }, [page])

    //  const nextPage = () => setPage(prev => prev+1)
    //const previewPage = () => setPage(prev => prev-1)

    useEffect(() => {
        getTask()
    }, [getTask])


    if(loading){
        return <progress />
    }


    if(tasksData.length){

        return (
            <div style={{paddingTop: 100}}>
                <h1>Задачи</h1>
                {tasksData.map((task) => 
                    <div class="card" style={{width: "18rem;", paddingTop: 20}}>
                    <div class="card-body">
                      <h5 class="card-title">{task.username}</h5>
                      <h6 class="card-subtitle mb-2 text-muted">{task.email}</h6>
                      <p class="card-text">{task.text}</p>
                    </div>
                  </div>
                )}
            </div>

        )
    }

    return <p>Задач нет, добавляйте</p>
}
