import {Modal} from "react-bootstrap"



export const ModalWindow = (data) => {

    const handleClose = () => !data.show


    return (
<Modal show={data.show} onHide={handleClose}>
        <Modal.Header closeButton>
            {data.message}
        </Modal.Header>
</Modal>
    )

}
