import axios from 'axios'
import {useState, useCallback, useEffect} from "react"

export const TasksPage = () => {
    const [tasksData, setTasksData] = useState([])
    const [loading, setLoading] = useState(false)
    const page = 1

    const getTask = useCallback(async () => {
        try{
            setLoading(true)
            await axios.get(
                `/api/tasks/?page=${page}`
                ).then((response) => {
                    setLoading(false)
                    setTasksData(response.data.message.tasks)

                }, [page])  

        }catch(e){}

    }, [])


    const nextPage = () => {
        page+1
    }

    useEffect(() => {
        getTask()
    }, [getTask])


    if(loading){
        return <progress />
    }


    if(tasksData.length){
        return (
        <div style={{paddingTop: 50}}>
            <h1>Задачи</h1>

            {tasksData.map((task) => {
                return (

                    <div class="card" style={{width: "18rem;", marginTop: 10}} key={task.id}>
                        <div class="card-body">
                            <h5 class="card-title">{task.username}</h5>
                                <h6 class="card-subtitle mb-2 text-muted">{task.email}</h6>
                                <p class="card-text">{task.text}</p>
                        </div>
                    </div>
                )
            })}

            <button onClick={nextPage}>Next</button>
        </div>
        )
    }

    return <p>Задач нет, добавляйте</p>
}
