export const Paginate = (data) => {

    const pages = Number(data.numTasks) / 3
    const style = {
        marginTop: 10
    }


    if(1 !== data.page){
        console.log(Math.ceil(pages))
        return <button style={style} className="page-link" onClick={data.previewButton}>Previous</button>
    }

    if(Math.ceil(pages) !== data.page){
        return <button style={style} className="page-link" onClick={data.nextButton}>Next</button>
    }


}
