import {useState} from 'react'
//import {Navigate} from 'react-router-dom'
import axios from 'axios'
import {ModalWindow} from "../component/modal"


export const CreatePage = () => {

    //const [navigate, setNavigate] = useState( false )
    const [message, setMessage] = useState('')
    const [show, setShow] = useState(false)
    const [form, setForm] = useState({
        username: '', email: '', text: ''
    })



    const changeHandler = (event) => {
        setForm({...form, [event.target.name]: event.target.value})
    }
    const pressHandler = async () => {
        try{

            const formData = new FormData();
                formData.append("username", form.username);
                formData.append("email", form.email);
                formData.append("text", form.text);

            await axios({
                url: 'https://uxcandy.com/~shapoval/test-task-backend/v2/create?developer=ilia',
                crossDomain: true,
                    method: 'POST',
                    mimeType: "multipart/form-data",
                    contentType: false,
                    processData: false,
                    data: formData,
                    dataType: "json",
            }).then((response) => {
                //setNavigate(true)
                setMessage(response.data.message)
                setShow(true)
            })


        }catch(e){}
    }



    return (
        <form style={{paddingTop: 100, maxWidth: 500, margin: 'auto'}} >
            <ModalWindow 
            message={message}
            show={show}
            />
            <div className="mb-3">
                <label htmlFor="exampleFormControlInput2" className="form-label">Username</label>
                <input type="text" className="form-control" name='username' id="exampleFormControlInput2" onChange={changeHandler} />
            </div>
            <div className="mb-3">
                <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
                <input type="email" className="form-control" name='email' id="exampleFormControlInput1" placeholder="name@example.com" onChange={changeHandler}/>
            </div>
            <div className="mb-3">
                <label htmlFor="exampleFormControlTextarea1" className="form-label">Example textarea</label>
                <textarea className="form-control" name='text' id="exampleFormControlTextarea1" rows="3" onChange={changeHandler}/>
            </div>
            <button className="w-100 btn btn-lg btn-primary" onClick={pressHandler} type='button'>Sign in</button>
        </form>
    )
}
