import {useState, useContext} from 'react'
import {AuthContext} from "../context/authContext"
import axios from "axios"

//<div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
export const LoginPage = () => {

    const auth = useContext(AuthContext)


    const [disable, setDisable] = useState(true)
    const [form, setForm] = useState({
        username: '', password: ''
    })

    const lol = () => setDisable(false)

    const changeHandler = event => {
        setForm(
        {...form, [event.target.name]: event.target.value})
    }



    const loginHandler = async () => {
        try {
            const formData = new FormData();
            formData.append("username", form.username);
            formData.append("password", form.password);

        await axios({
            url: 'https://uxcandy.com/~shapoval/test-task-backend/v2/login?developer=ilia',
            crossDomain: true,
                method: 'POST',
                mimeType: "multipart/form-data",
                contentType: false,
                processData: false,
                data: formData,
                dataType: "json",
        })
          .then((data) => auth.login(data.data.message.token))  


        } catch (e) {}

    }
    return (
        <div style={{paddingTop: 170}}>
            <div className="text-center" cz-shortcut-listen="true">
                <div className="form-signin" style={{maxWidth: 350, margin: 'auto'}}>
                    <form>

                        <h1 className="h3 mb-3 fw-normal">Вход</h1>

                        <div className="form-floating">
                            <input type="text" className="form-control" id="floatingInput" placeholder="name@example.com" name='username' onChange={changeHandler}/>
                            <label htmlFor="floatingInput">Username</label>
                        </div>
                        <div className="form-floating">
                          <input type="password" className="form-control" id="floatingPassword" placeholder="Password" name='password' onChange={changeHandler}/>
                          <label htmlFor="floatingPassword">Password</label>
                        </div>


                        <button className="w-100 btn btn-lg btn-primary" disabled={disable} type="button" onClick={loginHandler}>Sign in</button>

                    </form>
                </div>
            </div>
        </div>
    )
}
